window.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".menu"),t=document.querySelectorAll(".menu_item"),c=document.querySelector(".mobile_menu");c.addEventListener("click",()=>{c.classList.toggle("mobile_menu_active"),e.classList.toggle("menu_active")}),t.forEach(t=>{t.addEventListener("click",()=>{c.classList.toggle("mobile_menu_active"),e.classList.toggle("menu_active")})})});